<script>
const carousel = document.querySelector(".carousel-container");
const slides = document.querySelectorAll(".carousel__item");
let intervalId;

function handleCarouselMove(positive = true) {
    const slideWidth = slides[0].clientWidth;
    const totalWidth = slideWidth * slides.length;
    const maxScrollLeft = totalWidth - carousel.clientWidth;

    if (positive) {
        if (carousel.scrollLeft + slideWidth >= maxScrollLeft + slideWidth) {
            carousel.scrollLeft = 0; // Go to the start
        } else {
            carousel.scrollLeft += slideWidth;
        }
    } else {
        if (carousel.scrollLeft - slideWidth <= -slideWidth) {
            carousel.scrollLeft = maxScrollLeft; // Go to the end
        } else {
            carousel.scrollLeft -= slideWidth;
        }
    }

    // Clear interval when buttons are clicked and restart it
    clearInterval(intervalId);
    startAutoMove();
}

// Function to move the carousel automatically
function autoMoveCarousel() {
    handleCarouselMove(true); // Move to the next slide
}

// Set interval to move the carousel every 5 seconds
function startAutoMove() {
    intervalId = setInterval(autoMoveCarousel, 2000);
}

// Clear interval when mouse enters the carousel
function pauseAutoMove() {
    clearInterval(intervalId);
}

// Restart interval when mouse leaves the carousel
function resumeAutoMove() {
    startAutoMove();
}

// Start auto movement initially
startAutoMove();

// Add event listeners for hover
carousel.addEventListener("mouseenter", pauseAutoMove);
carousel.addEventListener("mouseleave", resumeAutoMove);

// Add event listeners for buttons
document.getElementById("prevButton").addEventListener("click", () => handleCarouselMove(false));
document.getElementById("nextButton").addEventListener("click", () => handleCarouselMove(true));

</script>
